<script>
import { mapGetters, mapActions } from 'vuex'

export default {
  name: "SideBar",
  data: function() {
    return {
      isActive: [0, 0, 0, 0, 0, 0, 0]
    };
  },
  created() {
    this.optionChange(this.$route.path);
  },
  methods: {
    ...mapActions({
      flipControladorSidebar: 'flipControladorSidebar'
    }),
    handleChangePage(index) {
      this.isActive = [0, 0, 0, 0, 0, 0, 0];
      this.isActive[index] = 1;
    },
    optionChange(route) {
      this.isActive = [0, 0, 0, 0, 0, 0, 0];
      switch (route) {
        case "/dash/cases":
          this.isActive[0] = 1;
          break;
        case "/dash/perdidos":
          this.isActive[1] = 1;
          break;
        case "/dash/encontrados":
          this.isActive[2] = 1;
          break;
        case "/dash/buscar":
          this.isActive[3] = 1;
          break;
        case "/dash/meusItens":
          this.isActive[4] = 1;
          break;
      }
    },
     deslogar() {
      this.$router.push("/login");
    }
  },
  computed: {
    ...mapGetters({
      getControladorSidebar: 'getControladorSidebar'
    })
  }
};
</script>

